import { Page, Layout, TextStyle } from '@shopify/polaris';

const Index = () => (
  <Page>
    <Layout>
      <TextStyle variation="positive">
        Richard's Sample app using React and Next.js
      </TextStyle>
    </Layout>
  </Page>
);

export default Index;
